(13, '置业顾问', '<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">1</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">、</span><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">22-28</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">岁，女，大专以上学历；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">2</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">、身高</span><st1:chmetcnv hasspace="False" negative="False" numbertype="1" sourcevalue="1.6" tcsc="0" unitname="米" w:st="on"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">1.60</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">米</span></st1:chmetcnv><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">以上，五官端正，形象气质极佳，有较好的亲和力。</span></font><font color="#000000"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri"> <br />\r\n	3</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">、懂基本商务礼仪，具备出色的表达、沟通能力，口齿伶俐，思维敏捷。</span></font><font color="#000000"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri"> <br />\r\n	4</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">、具备良好的心理素质，勇于面对压力和挑战，有志于从事房地产销售事业。</span></font><font color="#000000"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri"> <br />\r\n	5</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">、勤奋敬业，为人热情积极，有较强的客户服务意识及团队合作意识。</span><span lang="EN-US" style="font-size: 9pt"><font face="Calibri"> <o:p></o:p></font></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">6</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">、提供完整商业地产知识培训。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri"><font color="#000000">待遇面谈</font></span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></p>\r\n', '不限', '2012-06-01 09:45:12', '2012-05-31 16:00:00', '2012-06-29 16:00:00', '女', '大专', '阳江市', 0),
(8, '总经理', '<p class="MsoNormal" style="text-indent: -18pt; margin: 0cm 0cm 0pt 18pt; mso-list: l0 level1 lfo1">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt; mso-fareast-font-family: calibri; mso-bidi-font-family: calibri"><span style="mso-list: ignore"><font face="Calibri">1、</font><span style="font: 7pt ''times new roman''">&nbsp;&nbsp; </span></span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">男性，年龄</span><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">40</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">周岁以上，大专以上学历；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: -18pt; margin: 0cm 0cm 0pt 18pt; mso-list: l0 level1 lfo1">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt; mso-fareast-font-family: calibri; mso-bidi-font-family: calibri"><span style="mso-list: ignore"><font face="Calibri">2、</font><span style="font: 7pt ''times new roman''">&nbsp;&nbsp; </span></span></span><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">10</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">年以上大型房地产公司项目总经理的工作经验；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: -18pt; margin: 0cm 0cm 0pt 18pt; mso-list: l0 level1 lfo1">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt; mso-fareast-font-family: calibri; mso-bidi-font-family: calibri"><span style="mso-list: ignore"><font face="Calibri">3、</font><span style="font: 7pt ''times new roman''">&nbsp;&nbsp; </span></span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">熟悉项目的规划、报建、设计、施工及营销策划，有较高的市场敏锐度；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: -18pt; margin: 0cm 0cm 0pt 18pt; mso-list: l0 level1 lfo1">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt; mso-fareast-font-family: calibri; mso-bidi-font-family: calibri"><span style="mso-list: ignore"><font face="Calibri">4、</font><span style="font: 7pt ''times new roman''">&nbsp;&nbsp; </span></span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">能独立操作</span><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">20</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">万平米以上大型房产项目者优先。</span></font></p>\r\n<p class="MsoNormal" style="text-indent: -18pt; margin: 0cm 0cm 0pt 18pt; mso-list: l0 level1 lfo1">\r\n	&nbsp;</p>\r\n<p>\r\n	待遇面谈</p>\r\n', '1', '2012-06-01 03:34:58', '2012-06-01 16:00:00', '2012-06-29 16:00:00', '男', '大专', '阳江市', 5),
(10, '工程部经理', '<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">1</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">、男性，年龄</span><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">35</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">周岁以上，大专以上学历，工民建或建筑类专业。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">2</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">、</span><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">5</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">年以上大型房地产公司同岗位工作经验。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">3</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">、本专业高级技术职称。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">4</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">、具有高度的责任心并熟悉房产公司工程管理的各项流程。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri"><font color="#000000">待遇面谈</font></span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></p>\r\n', '1', '2012-06-01 09:38:33', '2012-05-31 16:00:00', '2012-06-29 16:00:00', '男', '大专', '阳江市', 4),
(11, '土建工程师', '<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">1</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">、男性，大专或以上学历，五年以上开发商同职工作经验，具有专业土建结构、工民建等专业技术职称；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">2.</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">、熟悉现场管理、施工规范；</span><span lang="EN-US" style="font-size: 9pt"><font face="Calibri"> <o:p></o:p></font></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">3</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">、</span><span style="font-size: 9pt"><font face="Calibri"> </font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">具有良好的组织、沟通、协调和执行能力，工作思路清晰；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">4</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">、具有极强的责任心和优秀的组织能力，具有良好的职业道德、诚实自律，并具备出色的团队合作精神；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">5</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">、能熟练操作</span><span lang="EN-US" style="font-size: 9pt"><font face="Calibri">AutoCAD</font></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">等办公软件。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri"><font color="#000000">待遇面谈</font></span></p>\r\n', '2', '2012-06-01 09:40:11', '2012-05-31 16:00:00', '2012-06-29 16:00:00', '男', '大专', '阳江市', 4),
(14, '招商主任', '<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	1、女，23岁-35岁，2人，阳江本地人，会白话；<br />\r\n	2、大专学历以上，市场营销、房地产、运营管理等专业优先；<br />\r\n	3、五官端正，形象气质极佳，有较好的亲和力；<br />\r\n	4、懂基本商务礼仪，具备出色的沟通能力、谈判能力与客情维护能力，口齿伶俐，思维敏捷；<br />\r\n	5、具备良好的心理素质，勇于面对压力和挑战，熟悉商业地产业态，具备商业招商管理经验；<br />\r\n	6、勤奋敬业，为人热情积极，有较强的客户服务意识及团队合作意识。</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	待遇面谈</p>\r\n', '2', '2012-06-11 09:05:26', '2012-05-31 16:00:00', '2012-06-29 16:00:00', '女', '大专', '阳江市', 2),
(15, '招商综合管理员', '<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt">1</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">、女，</span><span lang="EN-US" style="font-size: 9pt">23-28</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">岁，大专学历，精通阳江话，市场营销、房地产、经营管理类专业优先；</span><span style="font-size: 9pt"> <span lang="EN-US"><o:p></o:p></span></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt">2</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">、从事房地产、家居建材、商业运营等行业的综合管理、业务销售者优先；</span><span style="font-size: 9pt"> <span lang="EN-US"><o:p></o:p></span></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt">3</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">、五官端正，形象气质较佳，有较好的亲和力，有较强的客户服务意识及团队合作意识；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt">4</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">、懂基本商务礼仪，具备出色的沟通能力与客情维护能力，口齿伶俐，思维敏捷；</span><span style="font-size: 9pt"> <span lang="EN-US"><o:p></o:p></span></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt">5</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">、勤奋敬业，为人热情积极，具备良好的心理素质。</span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">待遇面谈</span></font></p>\r\n', '1', '2012-06-14 08:39:23', '2012-05-31 16:00:00', '2012-06-29 16:00:00', '女', '大专', '阳江市', 3),
(16, '销售部经理', '<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span style="font-family: 宋体; color: #333333; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial; mso-bidi-font-family: arial">熟悉房地产销售的流程，有较强的营销经验和亲和力，具备一定的管理能力和协调能力，公司提供良好的工作环境和福利待遇。</span><span lang="EN-US" style="font-family: arial; color: #333333; font-size: 9pt"><o:p></o:p></span></p>\r\n<p>\r\n	<span style="font-family: 宋体; color: #333333; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial; mso-bidi-font-family: arial; mso-font-kerning: 1.0pt; mso-ansi-language: en-us; mso-fareast-language: zh-cn; mso-bidi-language: ar-sa">任职资格：</span><span lang="EN-US" style="font-family: arial; color: #333333; font-size: 9pt; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: en-us; mso-fareast-language: zh-cn; mso-bidi-language: ar-sa"><br />\r\n	1</span><span style="font-family: 宋体; color: #333333; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial; mso-bidi-font-family: arial; mso-font-kerning: 1.0pt; mso-ansi-language: en-us; mso-fareast-language: zh-cn; mso-bidi-language: ar-sa">、女性，本科及以上学历，市场营销等相关专业；</span><span lang="EN-US" style="font-family: arial; color: #333333; font-size: 9pt; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: en-us; mso-fareast-language: zh-cn; mso-bidi-language: ar-sa"><br />\r\n	2</span><span style="font-family: 宋体; color: #333333; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial; mso-bidi-font-family: arial; mso-font-kerning: 1.0pt; mso-ansi-language: en-us; mso-fareast-language: zh-cn; mso-bidi-language: ar-sa">、五年以上商业地产销售工作经验，同等职位两年以上工作经验，有较强的团队领导能力和各方面协调能力；</span><span lang="EN-US" style="font-family: arial; color: #333333; font-size: 9pt; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: en-us; mso-fareast-language: zh-cn; mso-bidi-language: ar-sa"><br />\r\n	3</span><span style="font-family: 宋体; color: #333333; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial; mso-bidi-font-family: arial; mso-font-kerning: 1.0pt; mso-ansi-language: en-us; mso-fareast-language: zh-cn; mso-bidi-language: ar-sa">、具备较强的市场分析、营销、推广能力和良好的人际沟通、协调能力，分析和解决问题的能力；</span><span lang="EN-US" style="font-family: arial; color: #333333; font-size: 9pt; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: en-us; mso-fareast-language: zh-cn; mso-bidi-language: ar-sa"><br />\r\n	4</span><span style="font-family: 宋体; color: #333333; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial; mso-bidi-font-family: arial; mso-font-kerning: 1.0pt; mso-ansi-language: en-us; mso-fareast-language: zh-cn; mso-bidi-language: ar-sa">、有较强的事业心和领导能力。</span><span lang="EN-US" style="font-family: arial; color: #333333; font-size: 9pt; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: en-us; mso-fareast-language: zh-cn; mso-bidi-language: ar-sa"><br />\r\n	5</span><span style="font-family: 宋体; color: #333333; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial; mso-bidi-font-family: arial; mso-font-kerning: 1.0pt; mso-ansi-language: en-us; mso-fareast-language: zh-cn; mso-bidi-language: ar-sa">、对制定销售计划、部署销售管理工作、房地产相关政策法规、销售流程、销售技巧等有丰富的经验和较强的执行力。</span></p>\r\n<p>\r\n	<span style="font-family: 宋体; color: #333333; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial; mso-bidi-font-family: arial; mso-font-kerning: 1.0pt; mso-ansi-language: en-us; mso-fareast-language: zh-cn; mso-bidi-language: ar-sa">待遇面谈</span></p>\r\n', '1', '2012-06-15 00:51:50', '2012-05-31 16:00:00', '2012-06-29 16:00:00', '女', '本科', '阳江市', 5);
INSERT INTO `maxtron_jobs_resume` (`id`, `job_id`, `applicant`, `gender`, `birthday`, `native`, `tall`, `landscape`, `wedding`, `cer_type`, `cer_num`, `working_age`, `education`, `school`, `professional`, `hope_wage`, `p_company`, `p_post`, `p_wage`, `address`, `email`, `phone`, `work_ex`, `edu_ex`, `createtime`) VALUES
(46, 13, '茹艳芳', 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '18948527303', '个人简历							\r\n							\r\n姓名	茹艳芳	性别	女	出生年月日	1988.03.10		\r\n籍贯：	广东	民族	汉	婚姻状况	未婚		\r\n联系方式：							\r\n电话：	15820340858		地址：	广东省阳东县合章路			\r\n教育背景：							\r\n2003.9-2006.7		阳东中学			高中		\r\n2011		阳江广播电视大学			大专		\r\n工作经历：							\r\n2006年-2008年		好亦多商场			领班		\r\n2008年-2010年		小太阳宝宝屋			店长		\r\n2010年-2011年		富力精密时计有限公司			采购文员		\r\n2011年-2012年		阳东四季物业管理有限公司			客服员		\r\n2012到今		阳江溢信汽车销售维修有限公司			销售顾问		\r\n离职原因：		寻找新的突破点，实现人生价值					\r\n个人爱好：		管理学，心理学，文学，音乐。					\r\n个人评价：		积极向上，有原则性和责任心，适应能力强，擅于言谈和交际，为人处事较稳重！					\r\n请给我一次机会，我将还您一片光彩！							\r\n							\r\n', NULL, '2012-08-11 20:40:09'),
(47, 10, '曾国祥', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '13926316656', '浙江温州人，2002年江西科大专修学院土木工程专业本科毕业，在施工单位（苍南县第六建筑工程公司）工作6年，在房地产公司工作4年，2010年起至今在阳江名扬国际广场任土建工程师兼监理部总监代表（因本项目监理公司为甲方挂靠）。', NULL, '2012-08-16 10:55:47');
INSERT INTO `maxtron_notice` (`id`, `title`, `icon`, `content`, `cate_id`, `datetime`, `top`, `digest`, `state`, `sort`, `view`) VALUES
(5, '桩深新业起，基实华科兴', '/uploads/images/new01.jpg', '<p>\r\n	　　华科国际家居生活广场项目建设秉承华科集团一向&ldquo;高效、专业&rdquo;的工作作风，工地上机声隆隆，建筑材料归置整齐，施工秩序有条不紊，8月6日开工至今已有四个月，真可谓&ldquo;日新月异&rdquo;。各工种、各部门齐力配合，加班加点，以分秒必争的姿态全力推进各项工作，为粤西南家居建材专业大市场早日开业添砖加瓦。<br />\r\n	　　&ldquo;桩深新业起，基实华科兴。&rdquo;华科国际全面进入施工建设阶段，标志着华科国际项目建设取得阶段性成果，也预示着华科国际这艘粤西南家居建材专业市场航母正式从蓝图一步步走向现实。<br />\r\n	&nbsp;</p>\r\n<p style="text-align: center">\r\n	<img alt="" height="261" src="/uploads/images/1.jpg" width="571" /></p>\r\n', 21, '2011-08-06', 0, 0, 1, 0, 1),
(6, '华科国际项目推进小组一行来访', '', '<p>\r\n	　　9月16日上午，由阳东县委、县政府牵头，各主要职能部门领导组成的华科国际家居生活广场项目推进小组成员一行11人到访华科国际接待中心。华科集团董事长林乃科、总经理林成贤参加了座谈，林乃科董事长表示，华科国际作为华科集团华南布局的重要项目，将在粤西南地区做到&ldquo;六个第一&rdquo;，进而实现&ldquo;六赢&rdquo;，即在粤西南同类市场中做到&ldquo;规模最大、档次最高、产品最新颖、品种最齐全、价格最合理、服务最完善&rdquo;，实现&ldquo;投资者、经营者、消费者、政府、企业、社会&rdquo;多方共赢。县委办有关领导表示，华科国际是阳东县对外招商引资重大项目，阳江市重点项目，自筹建之日起，县委、县政府就给予了高度重视，并抽调各主要职能部门的领导骨干，成立项目推进小组，全面协调项目建设中的各种问题，全力推动项目建设进度，确保早日落成。<br />\r\n	<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt="" height="305" src="/uploads/images/14.jpg" width="560" /></p>\r\n', 21, '2011-09-16', 0, 0, 1, 0, 1),
(7, '阳东县政协200余人考察团莅临华科国际参观考察', '', '<p>\r\n	　　11月8日，正值阳东县政协五届一次会议胜利召开之际，下午3点，政协委员200余人组成的考察团莅临华科国际家居生活广场营销中心考察访问。公司总经理林成贤、常务副总林乃年、办公室主任刘霞、策划总监商波炎陪同会见了考察团一行。<br />\r\n	　　营销中心工作人员向考察团介绍了我公司的基本情况和项目的未来规划，考察团成员先后参观了项目模型、观看了项目宣传片，对我公司的市场开发及经营实力、项目开发理念及建筑规划给予了高度的肯定。<br />\r\n	&nbsp;&nbsp;<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt="" height="351" src="/uploads/images/3.jpg" width="485" /></p>\r\n', 21, '2011-11-08', 0, 0, 1, 0, 1),
(8, '阳东县人大200余人代表团莅临华科国际考察指导工作', '', '<p>\r\n	&nbsp;　　11月9日下午3点半，阳东县人大代表一行200余人在阳东县人大七届一次会议胜利召开之际，组团到访华科国际家居生活广场营销中心。公司总经理林成贤、常务副总林乃年、办公室主任刘霞、策划总监商波炎陪同会见了代表团一行。<br />\r\n	　　营销中心工作人员向代表团详细介绍了华科国际家居生活广场的市场定位、规划优势、经营策略等项目情况，引导代表团成员参观了项目模型、观看项目宣传片。县人大代表对我公司的市场开发及经营实力、项目开发理念及建筑规划给予了高度的评价。<br />\r\n	&nbsp;&nbsp;<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt="" height="331" src="/uploads/images/4.jpg" width="479" /></p>\r\n', 21, '2011-11-09', 0, 0, 1, 0, 1),
(9, '华科聘请中旭金牌讲师提升企业执行力', '', '<p>\r\n	　　11月23日，华科请来了中旭教育集团的金牌讲师孙文龙先生，为我司的团队执行力巩固与提升进行专业培训。下午2点30分，培训在华科国际会议厅正式开始，内容以&ldquo;西点军校&rdquo;执行力为例，主要就影响公司管理执行力的三大因素：机制、文化、能力进行了重点讲解分析和互动。<br />\r\n	　　我司集团董事长林乃科先生亲临现场，公司常务副总林乃年、办公室主任刘霞、营销总监林典峰、策划总监商波炎等管理层和公司员工一起进行了学习。<br />\r\n	<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt="" height="271" src="/uploads/images/5.jpg" width="508" /></p>\r\n', 21, '2011-11-23', 0, 0, 1, 0, 1),
(10, '东城镇“华科国际”杯元旦男子篮球赛、男女混合拔河赛启动', '/uploads/images/new02.jpg', '<p>\r\n	　　艳阳高照，清风起舞。12月8日下午4时，由阳东县东城镇主办、华科国际冠名赞助的&ldquo;华科国际&rdquo;杯元旦男子篮球赛、男女混合拔河赛启动仪式在该镇政府大院灯光球场顺利举行。东城镇党委书记、镇长出席了启动仪式并发表讲话，我司总经理林成贤先生发表了热情洋溢的致辞，预祝此次比赛取得圆满成功。<br />\r\n	　　比赛持续到夜幕降临，吸引了周边群众前来观看。第一场由金村端陶队对华科国际队，球场上双方队员施展球技，步步紧逼，卡位传球、带球过人、三步上篮，精彩的进球不时赢得观众的掌声和喝彩声，双方啦啦队也是满怀激情，适时加油鼓劲，比分曾几度持平。<br />\r\n	　　据了解，为了迎接元旦佳节，阳东县东城镇委、镇政府从12月8日到13日举办&ldquo;华科国际&rdquo;杯男子篮球赛，采用小组循环初赛和交叉决赛两个阶段，共有来自全镇的12支队伍参加；男女混合拔河比赛共有5支队伍参加。篮球赛的举办有力的推进了和谐东城建设进程，为元旦佳节的到来增添了喜庆的气氛。<br />\r\n	<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt="" height="259" src="/uploads/images/11.jpg" width="472" /><br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n', 21, '2011-12-08', 0, 0, 1, 0, 1),
(11, '华科国际积极参与广东省扶贫济困日活动', '', '<p>\r\n	　　日前，华科国际家居生活广场慷慨解囊，捐款20万元，参与广东省扶贫济困日活动。华科集团董事长林乃科先生代表华科国际进行了捐赠，并接受阳江电视台记者采访。<br />\r\n	　　林乃科董事长表示，华科集团奉行&ldquo;经世济民&rdquo;的公益慈善理念。经世济民是一种胸怀，更是一份责任。华科集团热衷于在普世价值观指导下的公益慈善事业，我们的企业宗旨之一，就是项目做到哪里，爱心就带到哪里。<br />\r\n	&nbsp;　　据不完全统计，自公司创业以来，华科集团累计无偿捐助资金、各类物资达3000余万元，用于社会医疗、教育、扶贫、赈灾等爱心事业，为社会和谐发展尽自己的绵薄之力。<br />\r\n	&nbsp;</p>\r\n', 21, '2011-12-08', 0, 0, 1, 0, 1),
(36, '阳江华科国际家居生活广场火爆招商中，欢迎来电垂询', '', '<p class="MsoNormal" style="margin: 0cm 0cm 0pt; mso-line-height-alt: 0pt">\r\n	<span style="font-family: 宋体; mso-bidi-font-size: 10.5pt; mso-bidi-font-family: 宋体; mso-font-kerning: 0pt"><font size="3"><font color="#000000"><span _fck_bookmark="1" style="display: none">&nbsp;</span></font></font></span><strong>一、招商策略<br />\r\n	</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 招商是项目经营成功的首要之义，华科集团采用捆绑型商业地产开发模式，通过选择开发同质性较强、可复制性较高的一批商业项目，与一大批商业协会、商会、知名企业、经营户建立长期战略合作关系，使之提前介入项目的整体设计，提供标准化的入驻平台。这种订单式的捆版合作，可以极大缩短合约谈判流程，减少招商、选商的时间成本，做到资源最大化，回报最大化，共赢最大化。华科集团竭诚欢迎广大商家入驻，共创双赢。</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; mso-line-height-alt: 0pt">\r\n	&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; mso-line-height-alt: 0pt">\r\n	<strong>二：招商信息<br />\r\n	</strong>阳江华科国际家居生活广场火爆招商中，欢迎来电垂询<br />\r\n	招商热线：0662-6336666,0662-6899991</p>\r\n', 76, '2012-02-07', 0, 0, 1, 0, 1),
(37, '华科集团2012年1到6月份招标、采购计划', '', '<p>\r\n	<strong>&nbsp;一、招标采购战略</strong></p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 随着华科集团旗下业务的飞速发展，项目招标、采购的作用日益凸显，采购工作已被华科集团从一般的传统采购提升到了战略采购的位置，并被视为企业的另一利润来源。本着&ldquo;公开透明、高效规范、降本节支&rdquo;的原则，华科集团致力于筛选具有竞争力、有知名品牌、产品高品质、质量高稳定的供应商，建立广泛的供应商网络和优秀的供应商群体，使各项目在采购中有丰富的供应渠道。<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 华科集团竭诚欢迎广大供应商，与我们建立长期的采购战略关系，采购事宜请电询华科集团采购部：0662-6888399 林先生</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong>二、最新采购计划</strong><br />\r\n	&nbsp;</p>\r\n<table border="1" cellpadding="0" cellspacing="0" class="MsoNormalTable" style="border-bottom: medium none; border-left: medium none; border-collapse: collapse; border-top: medium none; border-right: medium none; mso-border-alt: solid windowtext .5pt; mso-yfti-tbllook: 1184; mso-padding-alt: 0cm 5.4pt 0cm 5.4pt; mso-border-insideh: .5pt solid windowtext; mso-border-insidev: .5pt solid windowtext">\r\n	<tbody>\r\n		<tr style="mso-yfti-irow: 0; mso-yfti-firstrow: yes">\r\n			<td style="border-bottom: windowtext 1pt solid; border-left: windowtext 1pt solid; padding-bottom: 0cm; background-color: transparent; padding-left: 5.4pt; width: 142pt; padding-right: 5.4pt; border-top: windowtext 1pt solid; border-right: windowtext 1pt solid; padding-top: 0cm; mso-border-alt: solid windowtext .5pt" valign="top" width="189">\r\n				<p align="left" class="MsoNormal" style="text-align: left; margin: 0cm 0cm 0pt">\r\n					<font size="3"><font color="#000000"><span style="font-family: 宋体; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">项目</span><span lang="EN-US"><o:p></o:p></span></font></font></p>\r\n			</td>\r\n			<td style="border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; background-color: transparent; padding-left: 5.4pt; width: 142.05pt; padding-right: 5.4pt; border-top: windowtext 1pt solid; border-right: windowtext 1pt solid; padding-top: 0cm; mso-border-alt: solid windowtext .5pt; mso-border-left-alt: solid windowtext .5pt" valign="top" width="189">\r\n				<p align="left" class="MsoNormal" style="text-align: left; margin: 0cm 0cm 0pt">\r\n					<font size="3"><font color="#000000"><span style="font-family: 宋体; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">时间</span><span lang="EN-US"><o:p></o:p></span></font></font></p>\r\n			</td>\r\n			<td style="border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; background-color: transparent; padding-left: 5.4pt; width: 142.05pt; padding-right: 5.4pt; border-top: windowtext 1pt solid; border-right: windowtext 1pt solid; padding-top: 0cm; mso-border-alt: solid windowtext .5pt; mso-border-left-alt: solid windowtext .5pt" valign="top" width="189">\r\n				<p align="left" class="MsoNormal" style="text-align: left; margin: 0cm 0cm 0pt">\r\n					<font size="3"><font color="#000000"><span style="font-family: 宋体; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">文件</span><span lang="EN-US"><o:p></o:p></span></font></font></p>\r\n			</td>\r\n		</tr>\r\n		<tr style="mso-yfti-irow: 1">\r\n			<td style="border-bottom: windowtext 1pt solid; border-left: windowtext 1pt solid; padding-bottom: 0cm; background-color: transparent; padding-left: 5.4pt; width: 142pt; padding-right: 5.4pt; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm; mso-border-alt: solid windowtext .5pt; mso-border-top-alt: solid windowtext .5pt" valign="top" width="189">\r\n				<p align="left" class="MsoNormal" style="text-align: left; margin: 0cm 0cm 0pt">\r\n					<font size="3"><font color="#000000"><span style="font-family: 宋体; mso-ascii-font-family: calibri; mso-hansi-font-family: calibri">消防设备采购</span><span lang="EN-US"><o:p></o:p></span></font></font></p>\r\n			</td>\r\n			<td style="border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; background-color: transparent; padding-left: 5.4pt; width: 142.05pt; padding-right: 5.4pt; border-top: #000000; border-right: windowtext 1pt solid; padding-t