INSERT INTO `maxtron_notice` (`id`, `title`, `icon`, `content`, `cate_id`, `datetime`, `top`, `digest`, `state`, `sort`, `view`) VALUES
(111, '潜心砺剑十五载 厚积薄发经世用', '/uploads/images/%E5%A4%9A%E6%9C%9F%E6%8A%A5%E7%BA%B8%E6%96%87%E5%AD%97%E5%9B%BE%E7%89%87/201110250.jpg', '<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">潜心砺剑十五载</span></b><b style="mso-bidi-font-weight: normal"><span style="font-size: 12pt"> </span></b><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">厚积薄发经世用</span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;<img alt="" height="801" src="/uploads/images/多期报纸文字图片/201110250.jpg" width="549" /></font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">翻开华科集团企业发展史，我们看到是披荆斩棘、急流勇进的轨迹：</span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt">1996</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">年，创业于温州，从事基础投资建设及早期房地产开发业务；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt">2000</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">年，实现战略转型，商业地产成为公司主营业务；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt">2007</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">年，立足于东北、华东，在辽宁、山东、浙江、安徽、江苏等地开发了一系列省、市重点项目；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt">2010</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">年香港企业总部成立，与国内外商业领域多家知名企业形成战略合作伙伴关系；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt">2011</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">年，布局华南，投资建设阳江华科国际家居生活广场项目。</span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">&nbsp;&nbsp;&nbsp; 截至</span><span lang="EN-US" style="font-size: 9pt">2011</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">年，华科集团参与投资的商业项目总面积逾</span><span lang="EN-US" style="font-size: 9pt">350</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">万平方米，年产生交易额逾</span><span lang="EN-US" style="font-size: 9pt">245</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">亿元，年纳税达</span><span lang="EN-US" style="font-size: 9pt">12.2</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">亿元，解决就业人口</span><span lang="EN-US" style="font-size: 9pt">70000</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">多人，带动了一大批相关产业的发展，为项目所在地的区域经济繁荣贡献了自己的力量。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华科集团，正一步一个脚印地践行着自己&ldquo;义利统一、经世致用&rdquo;的企业哲学。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">高瞻远瞩</span><span lang="EN-US" style="font-size: 9pt"><span style="mso-spacerun: yes">&nbsp;&nbsp; </span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">提出&ldquo;三步走&rdquo;发展战略</span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">&nbsp;&nbsp;&nbsp; 第一步、立足江浙，以资源整合为主要特色的专业化发展阶段。</span><span lang="EN-US" style="font-size: 9pt">2000</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">年开始，华科顺利完成商业地产开发全程软硬件平台的建立及资源整合，开创性地提出了&ldquo;多元化投资、专业化开发&rdquo;的理念。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">第二步、面向全国，以规模扩张为主要内容的综合发展阶段。截止</span><span lang="EN-US" style="font-size: 9pt">2010</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">年底，华科成功完成企业项目运作程序标准化工作，先后在南京、合肥、大连、沈阳、济宁、阳江、贵州投资开发商业项目。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">第三步、境外上市，以资本运作及价值投资为导向的国际化发展阶段。在完成全国性规模扩张之后，华科将对旗下商业资源进行深度整合，并力争在</span><span lang="EN-US" style="font-size: 9pt">2016</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">年之前实现境外上市。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">布局华南</span><span lang="EN-US" style="font-size: 9pt"><span style="mso-spacerun: yes">&nbsp;&nbsp; </span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">迈入跨越式发展快车道</span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">&nbsp;&nbsp;&nbsp; 华科国际家居生活广场作为阳江市重点项目，是华科集团进入华南的第一个项目，也是实现&ldquo;三步走&rdquo;发展战略第二步的关键项目。为此，林乃科董事长亲自入驻项目负责整体开发推进，公司在人力、物力、财力等各方面予以全力倾斜。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><font color="#000000"><span style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp; </span><o:p></o:p></font></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><span style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">展望未来，年轻的华科集团，正以前所未有的活力和无以伦比的自信，迈入跨越式发展的快车道，用董事长林乃科先生的话说：&ldquo;我们所要做的、所能做的、必须做的，就是以生命为代价，把毕生的精力倾注于华科事业、倾入于商业地产、倾力于商业报国！&rdquo;</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华科伟业</span><span style="font-size: 9pt"> </span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">传世精品</span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">&nbsp;&nbsp;&nbsp; （华科集团部分开发项目）</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><span style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">台州新明国际家居生活广场</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><span style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">平湖中国服装城</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><span style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">舟山科来华红星美凯龙家居生活广场</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><span style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">宿迁水韵城</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><span style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">合肥用世生活城</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><span style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">阳江华科国际家居生活广场</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><font color="#000000"><span style="mso-spacerun: yes">&nbsp; </span><o:p></o:p></font></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">贵宾联线：</span><span lang="EN-US" style="font-size: 9pt">0662- 689 9999<o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">投资商：华科国际控股集团有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">开发商：阳江市华科市场投资有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">建筑规划：北京市建筑设计研究院深圳院</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">形象支持：深圳容纳广告</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">项目地址：阳江市广雅路与华科南路交汇处</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial"><font color="#000000">本广告图文仅供参考，所有文件均以政府最后批复文件为准</font></span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></p>\r\n', 21, '2011-10-25', 0, 0, 1, 0, 1),
(112, '华科国际 粤商来了！浙商来了!', '/uploads/images/%E5%A4%9A%E6%9C%9F%E6%8A%A5%E7%BA%B8%E6%96%87%E5%AD%97%E5%9B%BE%E7%89%87/201111220.jpg', '<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">粤商来了！浙商来了</span></b><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt">!<o:p></o:p></span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">侨胞也来了！&hellip;&hellip;</span></b><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p></o:p></span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;<img alt="" height="349" src="/uploads/images/多期报纸文字图片/201111220.jpg" width="500" /></font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华科国际家居生活广场，自</span><span lang="EN-US" style="font-size: 9pt">8</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">月</span><span lang="EN-US" style="font-size: 9pt">6</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">日开工以来，因其不可模仿的众多资源优势，强势吸引广州、深圳、佛山、中山、东莞等地包括</span><span lang="EN-US" style="font-size: 9pt">TOTO</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">、立邦漆、雷士照明、华艺灯饰、杰克丹妮等珠三角</span><span lang="EN-US" style="font-size: 9pt">200</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">余家知名品牌意向签约入驻，以及无锡、嘉兴、杭州、温州、义乌等长三角浙商考察团和东南亚海外侨胞考察团等各地客、商络绎不绝奔涌而来，考察、咨询项目情况，事实证明：华科国际汇聚顶级优势，顺应市场需求，声名远播，得到了海内外广大客户和商家的认可和青睐。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">珠三角厂家、经销商聆听置业顾问讲解项目情况，&ldquo;华科国际填补了粤西南一站式大型家居建材市场的空白，规模之巨大，规划之超前，理念之先进，区位之优越，前景之广阔以及专业市场的准确定位都是我们商家入驻的首选。&rdquo;</span><span lang="EN-US" style="font-size: 9pt">TOTO</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">厂家一位负责人给出了专业的分析。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">长三角浙商考察团莅临接待中心考察现场，其中一位客商说&ldquo;华科国际由我们浙商知名企业&mdash;&mdash;华科集团投资开发，该集团在东北、华北、华东长三角等地区开发了众多被评为&lsquo;省、市重点项目&rsquo;的专业市场，并且经营的很不错，我们多次的投资都得到了相当高的收益，开发和经营实力我们信得过，所以我们这次又来了。&rdquo;</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">海外侨胞客商聚在模型周围了解项目规划，纷纷表示出对项目的兴趣，对项目的规划和市场前景满怀信心：&ldquo;华科国际作为粤西南首屈一指的专业市场，这样的专业市场铺投入少、风险低、回报快、收益高，买铺，当然还是首选专业市场铺。&rdquo;</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">贵宾联线：</span><span lang="EN-US" style="font-size: 9pt">0662- 689 9999<o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">投资商：华科国际控股集团有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">开发商：阳江市华科市场投资有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">建筑规划：北京市建筑设计研究院深圳院</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">形象支持：深圳容纳广告</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">项目地址：阳江市广雅路与华科南路交汇处</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial"><font color="#000000">本广告图文仅供参考，所有文件均以政府最后批复文件为准</font></span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></p>', 21, '2011-11-22', 0, 0, 1, 0, 1),
(113, '华科国际 封顶在即 指日可待', '/uploads/images/%E5%A4%9A%E6%9C%9F%E6%8A%A5%E7%BA%B8%E6%96%87%E5%AD%97%E5%9B%BE%E7%89%87/201112140.jpg', '<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">封顶在即</span></b><b style="mso-bidi-font-weight: normal"><span style="font-size: 12pt"> </span></b><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">指日可待</span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;<img alt="" height="348" src="/uploads/images/多期报纸文字图片/201112140.jpg" width="500" /></font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华科国际以稳健的施工速度赢得广大商家的肯定！</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华科国际家居生活广场，开工至今已四月有余，施工建设风雨无阻，马不停蹄，真可谓&ldquo;日新月异&rdquo;。目前工程一期已有部分封顶，预计</span><span lang="EN-US" style="font-size: 9pt">12</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">月底二层封顶将陆续完成</span><span lang="EN-US" style="font-size: 9pt">10</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">栋。稳健的施工进度，吸引了慕名前来的广大厂家、经销商的驻足，纷纷表示：华科国际工程进度如此神速，使我们商家的进驻充满了信心，宏基伟业突飞猛进，巨擘启航指日可待！</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">配图：</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华科集团董事长林乃科审核工程进度</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">即将封顶商铺局部鸟瞰</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">忙碌的施工现场，封顶在即</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">图纸研究，一丝不苟</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">即将封顶商铺的外立面</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">专业的施工团队，整齐划一的建筑营房</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">干净的施工道路，整洁的施工办公环境</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">贵宾联线：</span><span lang="EN-US" style="font-size: 9pt">0662- 689 9999<o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">投资商：华科国际控股集团有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">开发商：阳江市华科市场投资有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">建筑规划：北京市建筑设计研究院深圳院</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">形象支持：深圳容纳广告</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">项目地址：阳江市广雅路与华科南路交汇处</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial"><font color="#000000">本广告图文仅供参考，所有文件均以政府最后批复文件为准</font></span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></p>', 21, '2011-12-14', 0, 0, 1, 0, 1);
INSERT INTO `maxtron_notice` (`id`, `title`, `icon`, `content`, `cate_id`, `datetime`, `top`, `digest`, `state`, `sort`, `view`) VALUES
(114, '华科国际 宏基伟业突飞猛进 施工建设探访记', '/uploads/images/%E5%A4%9A%E6%9C%9F%E6%8A%A5%E7%BA%B8%E6%96%87%E5%AD%97%E5%9B%BE%E7%89%87/201112160.jpg', '<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">宏基伟业突飞猛进</span></b><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p></o:p></span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华科国际家居生活广场施工建设探访记</span></b><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p></o:p></span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000"><img alt="" height="800" src="/uploads/images/多期报纸文字图片/201112160.jpg" width="547" />&nbsp;</font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华科国际家居生活广场，开工至今施工建设风雨无阻，一日千里。目前工程一期已有部分封顶，稳健的施工进度，吸引了各地慕名前来的投资商、经销商和厂家的驻足。华科国际工程进度如此神速，形象面貌&ldquo;日新月异&rdquo;，在保质保量的前提下，也为商家的早日进驻坚定了信心。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">到底是什么样的一个施工团队使这座民生工程突飞猛进？又是什么样的质量安全管理机制为这座宏基伟业保驾护航？为此，记者对华科国际的施工建设情况进行了较为详尽的了解。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">还未到达华科国际家居生活广场（以下简称：华科国际），就已看到远处华科国际工地内矗立的塔吊正上下左右的忙碌着。进入营销中心，华科国际兼管工程的周总说正要去工地，征得同意随行后，记者来到华科国际工程施工现场，从外观看，项目一期工程成果很明显，部分封顶在即，现场各司其职，整体工作进展顺利。据周总说，开工至今，施工单位华锦建设集团和海天建设集团分工明确，积极配合华科国际工程部在保质保量前提下抢工期。现按照原施工计划，年底之前可以分区完成部分基础施工、一层封顶和数栋二层封顶；展示大厅的基坑土方开挖已经完成，年前计划浇垫层全部完工。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">周总介绍，工程部包括经理、技术员、质检员、材料员、安全管理员等和他本人共</span><span lang="EN-US" style="font-size: 9pt">8</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">人，他们之间工作职责分明：组织人员认真熟悉图纸，与施工建设单位沟通现场用工及材料用量，提出相关人员及机具计划，在公司要求工期内制定详细的施工进度计划等等工作范围和内容明确。工程无小事，和其他部门相比加班是家常便饭，为保证工程质量和进度，工程部全体人员齐心协力，任劳任怨，经常加班至深夜</span><span lang="EN-US" style="font-size: 9pt">12</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">点甚至凌晨</span><span lang="EN-US" style="font-size: 9pt">2</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">点，周末也不例外，工作人员作息时间只能采用机动制。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">俗话说&ldquo;百年大计，质量第一&rdquo;。建筑工程产品是一种特殊产品，关系到千家万户的生命财产安全，其质量在任何时候、任何条件下都丝毫不能放松，所以工程项目的施工质量保障是重中之重。据在施工现场的工程部伍经理介绍，为了保障工程质量：华科国际引进了国家一级建筑企业&mdash;&mdash;华锦建设集团和特级建筑企业&mdash;&mdash;海天建设集团作为项目施工建设合作单位。从合作单位的团队管理来看，在加强思想政治教育、劳动纪律教育、职业道德教育、专业技术知识培训，健全岗位责任制，改善劳动保障条件，公平合理地激励劳动热情等方面，施工管理人员能及时组织学习有关技术标准、规范，统一思想，提高认识；能制定和完善各项规章制度，并认真执行；能定期召开碰头会，介绍各工段的施工情况及存在的问题，找出问题原因，及时安排下步工作；能明确施工人员的职责和权力，规范施工秩序，形成程序化管理。从政治素质、思想素质、业务素质和身体素质等方面综合考虑，施工单位能坚持严格把关质量与积极预防相结合，以防为主的方针，坚持高标准、严要求；以严为主的管理思想，严把人、机、料、法、环的控制，把关各道工序施工质量，严把隐蔽工程的质量关，严把重点部位的质量关，严把材料、设备、成品、半成品的质量关，严把竣工验收关。能够全面控制以人的工作质量来确保工程质量，这种管理机制为打造优良的建筑品质提供了坚实可靠的有力保障。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">同样，在安全管理方面更是不容忽视的。据了解，华科国际工程部实行&ldquo;层层落实，责任到人&rdquo;的管理措施，为保障&ldquo;阳江市重点项目&rdquo;&mdash;&mdash;华科国际家居生活广场这座民生工程保质保量达到市优良工程的标准，彻底贯彻&ldquo;安全第一，预防为主&rdquo;的施工政策，通过监理单位进行安全思想教育，提高安全意识，以确保零事故。此外，施工现场还开展&ldquo;文明工地，无烟工地&rdquo;等精神文明建设，以力争打造一个文明工程为目标。安全合理的管理方法可使工作事半功倍，正是这些防范于未然且行之有效的管理机制为阳江市这座商业巨擘的顺利施工保驾护航，使得华科国际拔地而起，施工建设顺风顺水，一日千里。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">周总还说到华科集团董事长<st1:personname productid="林乃科" w:st="on">林乃科</st1:personname>先生心系工程，为使宏伟蓝图尽快实现成为宏基伟业，经常进入工程施工现场，了解工程施工情况，监督施工进度，亲自把关建筑质量问题，对项目工程建设倾心倾力。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">谈到一期工程封顶在即，&ldquo;华科国际项目能取得如此顺利的进展，得感谢各部门的紧密配合、通力合作。&rdquo;周总感慨的说，作为阳东县政府招商引资重点项目、阳江市重点项目，在各级政府的大力支持下，各部门尽力开展各项工作，像供电局，不仅帮他们解决了施工中的一些问题，还简化相关手续等等相关部门的配合与帮助很多，这也大大加快了项目建设进度。如今，各部门通力合作，施工建设井然有序，取得了卓有成效的阶段性成果，也为确保项目明年开业争取了时间、奠定了基础。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">看到了华科国际日新月异的工程全貌，了解了工程建设专业严谨的施工团队，记者已经感受到华科国际正在为肩负起粤西南家居建材流通业奠定&ldquo;行业标杆&rdquo;和&ldquo;中国建材家居示范市场&rdquo;的使命，引领区域同类市场进入品牌化、专业化、体验式的全新购物时代，以及对核心辐射圈内的商业和居住环境的改善、升级产生深远影响，而脚踏实地一步步的实现它&ldquo;经世用&rdquo;的宏伟目标，正如全国人大常委会办公厅秘书局局长杨绍丞所题：&ldquo;华科国际民生所仰，宏基伟业雄起阳江！&rdquo;</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华科国际部分工程即将封顶</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华科国际工程副总周总和尹工身体力行，亲自督战施工建设</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华科国际施工现场掠影</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">展示大厅基坑基础施工</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">施工员合力浇灌泥浆</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">忙碌的封顶作业现场</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">施工员整理钢材</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">立体作业，塔吊林立</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华锦建设集团安全管理制度宣传</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">海天建设集团专业、整洁的建筑营房</span><span lang="EN-US" style="font-size: 9pt"><span style="mso-tab-count: 2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">贵宾联线：</span><span lang="EN-US" style="font-size: 9pt">0662- 689 9999<o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">投资商：华科国际控股集团有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">开发商：阳江市华科市场投资有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">建筑规划：北京市建筑设计研究院深圳院</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">形象支持：深圳容纳广告</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial"><font color="#000000">项目地址：阳江市广雅路与华科南路交汇处</font></span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></p>', 21, '2011-12-16', 0, 0, 1, 0, 1),
(115, '华科国际 龙腾中国年 华科贺新春', '/uploads/images/%E5%A4%9A%E6%9C%9F%E6%8A%A5%E7%BA%B8%E6%96%87%E5%AD%97%E5%9B%BE%E7%89%87/201201070.jpg', '<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">龙腾中国年</span></b><b style="mso-bidi-font-weight: normal"><span style="font-size: 12pt"> </span></b><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华科贺新春</span></b><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p></o:p></span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华科国际家居生活广场恭祝阳江人民新年快乐！</span></b><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p></o:p></span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt">VIP</span></b><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">诚意登记即将开启！</span></b><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p></o:p></span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;<img alt="" height="481" src="/uploads/images/多期报纸文字图片/201201070.jpg" width="700" /></font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">&nbsp;&nbsp;&nbsp; 华科国际家居生活广场，从筹划蓝图、盛大开工到部分封顶，各项工作开展顺利、井然有序，工程建设步步推进、稳扎稳打，按照计划取得了卓有成效的阶段性战绩：春节前将分区完成数栋二层封顶、一层封顶和部分基础施工；展示大厅部分在基坑土方开挖完工基础上完成浇垫层作业。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><span style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">铸商业旗舰盛世启航，立宏基伟业造福民生，华科国际家居生活广场</span><span lang="EN-US" style="font-size: 9pt">VIP</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">诚意登记即将开启，敬请期待！在此，感谢阳东县、阳江市各级政府、行业内外各界人士给予华科国际的大力支持和关爱，华科国际全体工作人员恭祝您及家人新年快乐、龙年吉祥、万事如意！</span><span style="font-size: 9pt"> <span lang="EN-US"><o:p></o:p></span></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">贵宾联线：</span><span lang="EN-US" style="font-size: 9pt">0662- 689 9999<o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">投资商：华科国际控股集团有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">开发商：阳江市华科市场投资有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">建筑规划：北京市建筑设计研究院深圳院</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">形象支持：深圳容纳广告</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial"><font color="#000000">项目地址：阳江市广雅路与华科南路交汇处</font></span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></p>\r\n', 21, '2012-01-07', 0, 0, 1, 0, 1),
(116, '华科国际 政府春风沐华科 新年开工当门红', '/uploads/images/%E5%A4%9A%E6%9C%9F%E6%8A%A5%E7%BA%B8%E6%96%87%E5%AD%97%E5%9B%BE%E7%89%87/201201080.jpg', '<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">政府春风沐华科</span></b><b style="mso-bidi-font-weight: normal"><span style="font-size: 12pt"> </span></b><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">新年开工当门红</span></b><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p></o:p></span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">&mdash;&mdash;华科国际新年开工第一时间迎来政府四套班子领导现场办公</span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;<img alt="" height="519" src="/uploads/images/多期报纸文字图片/201201080.jpg" width="700" /></font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">《阳江市</span><span lang="EN-US" style="font-size: 9pt">2012</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">年计划草案的报告》中明确指出，华科国际家居生活广场列为阳江市商贸物流业发展重点项目，将着力推进建设。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><st1:chsdate day="3" islunardate="False" isrocdate="False" month="2" w:st="on" year="2012"><span lang="EN-US" style="font-size: 9pt">2</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">月</span><span lang="EN-US" style="font-size: 9pt">3</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">日</span></st1:chsdate><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">下午，阳东县委书记、县人大常委会主任王作雄，阳东县委副书记、县长雷双富等党政四套班子领导，以及有关部门负责人一行</span><span lang="EN-US" style="font-size: 9pt">20</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">多人在投入新一年工作的第一时间来到华科国际家居生活广场建设工地现场办公，秉着对华科国际项目高度重视的精神，以及务实的工作作风帮助解决华科国际家居生活广场项目建设中遇到的困难。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">&ldquo;我们今天是来解决实际问题的。&rdquo;王作雄书记一下车便对前来迎接的华科集团董事长林乃科如是说，让华科国际的在场员工备受鼓舞。王作雄、雷双富等县领导一路走、一路看，来到华科国际家居生活广场建设现场，察看了项目首期工程建设的施工情况，详细了解了项目建设的进度，耐心倾听工程负责人的汇报，了解工程建设中遇到的问题。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">在随后召开的座谈会上，围绕目前首期工程已部分封顶、部分一层土建结顶的成果，就项目周边道路建设、项目工期安排、项目永久性用电等诸多项目建设中亟待解决的问题，王作雄、雷双富等县领导听取了华科集团负责人和县国土、建设、供电等部门的情况汇报。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">关于项目周边道路建设问题，雷县长指示，由东城镇协调、住建局负责尽快落实道路工程施工建设。会议首先就华科南路、华科北路的启动时间，目前进展程度以及招标计划完成时间进行了确定。华科南路施工图纸已经完成，正在进行财政审核，目前已完成招标工作，按计划，元宵过后施工队进场施工。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">雷县长针对项目永久用电及用电线路建设还提出了供电原则：方案最科学、时间最节省、经济最实惠。据此，供电局为保证华科国际装修开业用电需求，依据阳江市供电局的设计意图和供电现状制定了供电方案，已报市供电局审核，按照程序</span><span lang="EN-US" style="font-size: 9pt">15</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">天之内落实完方案审核和成本预算问题。会议还对供电施工安排、组织招标和组织施工等各工作环节进行了讨论并确定了各项工作节点。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">对于华科国际项目建设，雷县长要求各职能部门以高度的责任感，将华科国际项目建设作为自己的事情，尽心尽力的完成。并提出了两点意见：一、各部门按照项目推进分工、布署，做到各负其责，推进工作，解决问题，主动服务；二、要通力合作，全力以赴，需要审批的工作全部走绿色通道，需要研究的问题原则上两天内解决。针对工作对接问题，雷县长还提出了两点要求并建议确定固定模式：一、建立工作对接制度，由谭县长负责牵头，召集相关职能部门与华科国际每</span><span lang="EN-US" style="font-size: 9pt">10</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">天召开一次联席会议，进行充分的沟通、交流，尽量提前研究、考虑并尽快处理解决项目建设中出现的问题；二、建立项目建设进展情况每周通报制度，上报县委、县政府主要领导，同时抄报县人大、政协主要领导。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><span style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp; </span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">县委书记王作雄最后强调，华科国际已被列为阳东县政府</span><span lang="EN-US" style="font-size: 9pt">2012</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">年工作的重中之重，阳东三次产业结构能否进一步优化，第三产业的增加值能否突破新台阶，主要依靠重点项目，而华科国际首当其冲。同时，作为阳东县招商引资重点项目、阳江市重点项目，华科国际对于推动阳东可持续发展以及阳东物流业发展具有重要意义。因此，要求县相关部门和东城镇要高度重视华科国际家居生活广场项目，要一如既往地各负其责，各司其职，积极主动、高效优质的做好服务工作；各职能部门要通力协作，全力以赴，从人力、物力、财力全方位大力度做好支持工作，依法依规加快推进项目建设进度，力促项目早日投产、开业，发挥效益。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></p>\r\n<p>\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">贵宾联线：</span><span lang="EN-US" style="font-size: 9pt">0662- 689 9999<o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">投资商：华科国际控股集团有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">开发商：阳江市华科市场投资有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">建筑规划：北京市建筑设计研究院深圳院</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">形象支持：深圳容纳广告</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial"><font color="#000000">项目地址：阳江市广雅路与华科南路交汇处</font></span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></p>', 21, '2012-01-08', 0, 0, 1, 0, 1),
(117, '华科国际 铺王VIP登记正式启动 双重大礼等你拿', '/uploads/images/%E5%A4%9A%E6%9C%9F%E6%8A%A5%E7%BA%B8%E6%96%87%E5%AD%97%E5%9B%BE%E7%89%87/201202140.jpg', '<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><st1:chsdate day="18" islunardate="False" isrocdate="False" month="2" w:st="on" year="2012"><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt">2</span></b><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">月</span></b><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt">18</span></b><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">日</span></b></st1:chsdate><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p></o:p></span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">铺王</span></b><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt">VIP</span></b><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">登记正式启动</span></b><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p></o:p></span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">双重大礼等你拿</span></b><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p></o:p></span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;<img alt="" height="512" src="/uploads/images/多期报纸文字图片/201202140.jpg" width="700" /></font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">贵宾联线：</span><span lang="EN-US" style="font-size: 9pt">0662- 689 9999<o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">投资商：华科国际控股集团有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">开发商：阳江市华科市场投资有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">建筑规划：北京市建筑设计研究院深圳院</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">形象支持：深圳容纳广告</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial"><font color="#000000">项目地址：阳江市广雅路与华科南路交汇处</font></span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></p>', 21, '2012-02-14', 0, 0, 1, 0, 1),
(118, '华科国际 细节决定成败 品质铸就经典', '', '<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">细节决定成败，品质铸就经典</span></b><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p></o:p></span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">商业地产已经成为当今中国房地产行业的热点，纵观成功的商业地产项目开发，除了遵循&ldquo;选址是根本，定位是关键，运作是核心&rdquo;的基本原则，规划设计是否合理、细节布局是否处理得当显得尤为重要。商业建筑虽然属于一种高度市场化、商业化的建筑类型，但在创造体验消费场所这方面，特别重视建筑规划的艺术处理。对于大型商业地产项目而言，华科国际家居生活广场以筑造成为阳江地标性建筑物为目标，力争成为阳江具有鲜明文化内涵的建筑物及建筑群体，并创造延续建筑文脉与时代感相沟通的氛围。在建筑的商业空间效果营造中，调动一切艺术的、技术的手段，并着重于空间尺度、空间气氛的表现。在室内外空间的结合以及建筑与环境景观的结合等方面达到丰富性、多样性、趣味性的综合协调，并促进人与建筑及空间环境的互动性。</span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><span style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp; </span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华科国际家居生活广场，遵循商业地产重视的业态和功能布局的客观要求，一切以利于市场经营和满足商家需求为前提，以超前的规划设计水平，充分吸收了国内一流专业市场的设计灵感，确保经营的便捷性。使市场人流与物流的分流、水平交通与垂直交通的布局合理化、科学化，让整个市场在顺畅流动的前提下，不留任何死角，人流商流能遍布到每一商铺。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">抢铺热线：</span><span lang="EN-US" style="font-size: 9pt">0662-689 9999<o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">投资商：华科国际控股集团有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">开发商：阳江市华科市场投资有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">建筑规划：北京市建筑设计研究院深圳院</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">形象支持：深圳容纳广告</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">项目地址：阳江市广雅路</span><span lang="EN-US" style="font-size: 9pt">268</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">号（广雅中学北</span><span lang="EN-US" style="font-size: 9pt">1000</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">米）</span></font><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></p>', 21, '2012-04-05', 0, 0, 1, 0, 1);
INSERT INTO `maxtron_notice` (`id`, `title`, `icon`, `content`, `cate_id`, `datetime`, `top`, `digest`, `state`, `sort`, `view`) VALUES
(119, '华科国际 家居建材航母，扬帆启航', '/uploads/images/%E5%A4%9A%E6%9C%9F%E6%8A%A5%E7%BA%B8%E6%96%87%E5%AD%97%E5%9B%BE%E7%89%87/201204110.jpg', '<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><b style="mso-bidi-font-weight: normal"><span style="font-family: 宋体; font-size: 12pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">家居建材航母，扬帆启航</span></b><b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p></o:p></span></b></font></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;<img alt="" height="802" src="/uploads/images/多期报纸文字图片/201204110.jpg" width="578" /></font></o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">\r\n	<b style="mso-bidi-font-weight: normal"><span lang="EN-US" style="font-size: 12pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></b></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">华科国际家居生活广场雄踞粤西南中心枢纽城市&mdash;&mdash;阳江，项目</span><span lang="EN-US" style="font-size: 9pt">5</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">～</span><span lang="EN-US" style="font-size: 9pt">15</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">分钟车程内，西部沿海高速、沈海高速、云阳高速、</span><span lang="EN-US" style="font-size: 9pt">325</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">国道和建设中的广东沿海高铁，五大城际交通主干线汇聚围合，物流条件极其优越。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">项目总体规划建筑面积约</span><span lang="EN-US" style="font-size: 9pt">50</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">万㎡，是一座批发、零售相结合的家居建材源头专业市场，建成后将成为规模最大、档次最高、品种最齐全、产品最新颖、价格最合理、服务最完善的粤西南一站式家居建材专业大市场，核心目标辐射阳江及周边</span><span lang="EN-US" style="font-size: 9pt">50</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">分钟车程内阳春、阳西、海陵岛、恩平等城市</span><span lang="EN-US" style="font-size: 9pt">400</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">万人口的消费市场。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">随着商贸、旅游、娱乐的迅速发展及保利、恒大等房产名企的涌入，阳江已成为投资置业的热土，越发受到海内外华侨和各界成功人士的亲睐。恩平，处于阳江连接珠三角与粤西南的交通要道，从恩平市区出发，分别距开平</span><span lang="EN-US" style="font-size: 9pt">50</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">分钟、台山</span><span lang="EN-US" style="font-size: 9pt">1</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">小时</span><span lang="EN-US" style="font-size: 9pt">10</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">分钟、江门</span><span lang="EN-US" style="font-size: 9pt">1</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">个半小时车程，而距阳江本项目只需</span><span lang="EN-US" style="font-size: 9pt">30</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">分钟即达，高速方便快捷，对于阳江承接珠三角产业转移具有至关重要的战略作用，与阳江属于唇齿相依的关系。</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="text-indent: 18pt; margin: 0cm 0cm 0pt; mso-char-indent-count: 2.0">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">目前，华科国际项目工程一期家居建材步行街部分十余栋已全面封顶、</span><span lang="EN-US" style="font-size: 9pt">8.6</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">万平米的家居展示中心已完成地下室和一层主体施工，正在进行二层、三层主体施工，预计今年</span><span lang="EN-US" style="font-size: 9pt">6</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">月底主体封顶。招商销售形势喜人，国内一流家居建材品牌连锁巨头&mdash;&mdash;居然之家作为主力店已经正式签署入驻协议；龙头商家方面，华科国际已经考察评审了珠三角千余家居建材品牌商家，涵盖家具类、地板类、壁纸类、卫浴类、灯饰类、橱柜类、门业类等</span><span lang="EN-US" style="font-size: 9pt">23</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">个业态共上千个品牌，已与马可波罗、</span><span lang="EN-US" style="font-size: 9pt">TOTO</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">、科勒、诺贝尔、新明珠、罗莱、慕思、老板、欧派、圣象等几百家品牌商家签订了意向入驻协议。目前，项目已进入</span><span lang="EN-US" style="font-size: 9pt">VIP</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">客户诚意登记阶段，</span><span lang="EN-US" style="font-size: 9pt">5</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">月</span><span lang="EN-US" style="font-size: 9pt">26</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">日正式开盘发售！</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">财富巨舰扬帆启航，投资良机稍纵即逝！黄金旺铺蓄势待发！</span><span lang="EN-US" style="font-size: 9pt">5</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">月</span><span lang="EN-US" style="font-size: 9pt">26</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">日盛大开盘发售！</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><span style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">首付</span><span lang="EN-US" style="font-size: 9pt">10</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">万起，低成本，低风险，高成长旺铺；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><span style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">一手毛坯，</span><span lang="EN-US" style="font-size: 9pt">100%</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">产权，有房产证，首付</span><span lang="EN-US" style="font-size: 9pt">5</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">成，</span><span lang="EN-US" style="font-size: 9pt">10</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">年按揭；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><font color="#000000"><span style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp; </span><st1:chmetcnv hasspace="False" negative="False" numbertype="1" sourcevalue="6.2" tcsc="0" unitname="米" w:st="on">6.2<span lang="EN-US" style="font-family: 宋体; mso-ascii-font-family: arial; mso-hansi-font-family: arial"><span lang="EN-US">米</span></span></st1:chmetcnv><span lang="EN-US" style="font-family: 宋体; mso-ascii-font-family: arial; mso-hansi-font-family: arial">层高，买一层，赚一层；</span><o:p></o:p></font></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><span style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">买铺即收</span><span lang="EN-US" style="font-size: 9pt">3</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">年</span><span lang="EN-US" style="font-size: 9pt">24%</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">租金收益，即买即赚；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><span style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp; </span>10</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">年委托经营管理，无需费心打理，我做旺，您赚钱；</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span lang="EN-US" style="font-size: 9pt"><span style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp; </span>3</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">年</span><span lang="EN-US" style="font-size: 9pt">2</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">亿巨资培育市场，仅售四成，抢到赚到！</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span lang="EN-US" style="font-size: 9pt"><o:p><font color="#000000">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">抢铺热线：</span><span lang="EN-US" style="font-size: 9pt">0662-689 9999<o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">项目地址：阳江市广雅路</span><span lang="EN-US" style="font-size: 9pt">268</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">号（广雅中学北</span><span lang="EN-US" style="font-size: 9pt">1000</span><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">米）</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">投资商：华科国际控股集团有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">开发商：阳江市华科市场投资有限公司</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<font color="#000000"><span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial">建筑规划：北京市建筑设计研究院深圳院</span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt">\r\n	<span style="font-family: 宋体; font-size: 9pt; mso-ascii-font-family: arial; mso-hansi-font-family: arial"><font color="#000000">形象支持：深圳容纳广告</font></span><span lang="EN-US" style="font-size: 9pt"><o:p></o:p></span></p>\r\n', 21, '2012-04-11', 0, 0, 1, 0, 1),
(120, '华科国际 人人都有机会拿装修基金', '', '<p align="center" class="MsoNormal" style="text-align: center; line-height: 150%; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan">\r\n	<b><span style="line-height: 150%; font-family: 宋体; color: black; font-size: 12pt; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体">人人都有机会拿装修基金<span lang="EN-US"><o:p></o:p></span></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan">\r\n	<b><span style="font-family: 黑体; color: black; font-size: 12pt; mso-hansi-font-family: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体">华科国际首届家居建材交易会<span lang="EN-US"><o:p></o:p></span></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan">\r\n	<b><span style="font-family: 黑体; color: black; font-size: 12pt; mso-hansi-font-family: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体">盛大开启<span lang="EN-US"><o:p></o:p></span></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan">\r\n	<b><span lang="EN-US" style="font-family: 黑体; color: #222222; font-size: 12pt; mso-hansi-font-family: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体"><o:p>&nbsp;</o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan">\r\n	<b><span lang="EN-US" style="font-family: 黑体; color: #222222; font-size: 12pt; mso-hansi-font-family: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体"><o:p>&nbsp;</o:p></span></b></p>\r\n<p align="center" class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan">\r\n	<b><span lang="EN-US" style="font-family: 黑体; color: #222222; font-size: 12pt; mso-hansi-font-family: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体"><o:p>&nbsp;</o:p></span></b></p>\r\n<table align="left" border="1" cellpadding="0" cellspac